import React, { useEffect } from "react";
import { SiReact, SiIonic, SiAngular, SiJava, SiSpring, SiFirebase, SiGit, SiJenkins, SiAdobexd, SiAdobe, SiAdobeillustrator } from "react-icons/si";
import Overlay from "./Overlay";

const Myskills = () => {
  const skills = [
    { icon: <SiReact fontSize="25px" color="#61DBFB" />, title: "React Native" },
    { icon: <SiIonic fontSize="25px" color="#4E8EF7" />, title: "Ionic" },
    { icon: <SiAngular fontSize="25px" color="#DD0031" />, title: "Angular" },
    { icon: <SiJava fontSize="25px" color="#007396" />, title: "Java" },
    { icon: <SiSpring fontSize="25px" color="#6DB33F" />, title: "Spring Boot" },
    { icon: <SiFirebase fontSize="25px" color="#FFCA28" />, title: "Firebase" },
    { icon: <SiGit fontSize="25px" color="#F05032" />, title: "Git" },
    { icon: <SiJenkins fontSize="25px" color="#D24939" />, title: "Jenkins" },
    { icon: <SiAdobexd fontSize="25px" color="purple" />, title: "Adobe XD" },
    { icon: <SiAdobe fontSize="25px" color="#FF0000" />, title: "Adobe Photoshop" },
    { icon: <SiAdobeillustrator fontSize="25px" color="#FF7A00" />, title: "Adobe Illustrator" },
  ];

  useEffect(() => {
    // Your code that uses the openMenu variable
  }, []); // Add dependencies if needed

  return (
    <>
      <div id="my-skills"></div>
      <section className="mt-16 text-white relative">
        <div className="relative z-10 p-4">
          <h2 className="capitalize text-center">
            <span className="p-2 border-b text-xl border-gray-300 font-bold">
              ### My skills
            </span>
          </h2>
          <div
            className="relative m-auto mt-8"
            style={{ height: "420px", width: "300px" }}
          >
            {/* Replace the image with your desired representation */}
            <img
              className="object-cover h-full w-full"
              src="./assets/phone1.png"
              alt="skills displayed inside a phone"
            />
            <div className="grid grid-cols-2 gap-4 absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
              {skills.map((skill, index) => (
                <div key={index} className="text-gray-600 flex justify-center items-center flex-col">
                  {skill.icon}
                  <h6 className="text-sm">{skill.title}</h6>
                </div>
              ))}
            </div>
          </div>
        </div>
        <Overlay />
      </section>
    </>
  );
};

export default Myskills;
